import React from "react";

const ReportList = ({ reports }) => {
  return (
    <div className="report-list">
      <h2>üìë Uploaded Credit Reports</h2>
      {reports.length === 0 ? (
        <p>No reports uploaded yet.</p>
      ) : (
        reports.map((report, index) => (
          <div key={index} className="report-card">
            <h3>{report.name}</h3>
            <p>üìû Mobile: {report.mobile}</p>
            <p>üìÑ PAN: {report.pan}</p>
            <p>üìä Total Accounts: {report.totalAccounts}</p>
            <p>üí∞ Current Balance: ‚Çπ{report.currentBalance}</p>
            <h4>üîπ Credit Accounts</h4>
            <ul className="credit-accounts">
              {report.creditAccounts.map((acc, i) => (
                <li key={i}>
                  üè¶ {acc.bankName} | A/c: {acc.accountNumber} | Overdue: ‚Çπ{acc.amountOverdue}
                </li>
              ))}
            </ul>
          </div>
        ))
      )}
    </div>
  );
};

export default ReportList;
